import React, { useMemo } from 'react';

function Team({ Agenre, Bgenre, Cgenre }) {
  const candidates = [
    '민식',
    '무현',
    '대중',
    '기철',
    '회영',
    '민솔',
    '성묵',
    '희선',
    '대희',
    '영미',
    '소영',
    '알라',
  ];
  const randomCandidates = candidates.sort((a, b) => {
    return parseInt(Math.random() * 10) - 5;
  });

  const { teamA, teamB, teamC } = useMemo((candidateArray) => {
    const newCandidates = candidateArray.sort((a, b) => {
      return parseInt(Math.random() * 10) - 5;
    });
    const teamA = newCandidates.splice(0, 4);
    const teamB = newCandidates.splice(0, 4);
    const teamC = newCandidates.splice(0, 4);
    return teamA, teamB, teamC;
  });

  return (
    <div>
      {Agenre}팀: {teamA}
      <br />
      {Bgenre}팀: {newTeamB}
      <br />
      {Cgenre}팀: {newTeamC}
    </div>
  );
}

export default React.memo(Team);
