import React, { useState } from 'react';
import Team from './Team';

function App() {
  const [ganres, setGanre] = useState({ Agenre: '', Bgenre: '', Cgenre: '' });
  const [candidate, setCandidate] = useState({
    candidateName: [],
  });
  const { Agenre, Bgenre, Cgenre } = ganres;
  const { candidateName } = candidate;
  const onChanges = (e) => {
    const { value, name } = e.target;
    setGanre({
      ...ganres,
      [name]: value,
    });
  };

  const onInput = (e) => {
    const { value, name } = e.target;
    setCandidate({
      [name]: value,
    });
  };
  const enterPressed = (e) => {
    if (e.key === 'Enter') {
      saveButton();
    }
  };
  const saveButton = () => {
    const user = [candidateName];
    console.log(user);
    // setCandidate(candidateName.concat(user));
  };
  return (
    <>
      <div>
        팀A 장르 : <input name="Agenre" onChange={onChanges} value={Agenre} />
        팀B 장르 : <input name="Bgenre" onChange={onChanges} value={Bgenre} />
        팀C 장르 : <input name="Cgenre" onChange={onChanges} value={Cgenre} />
      </div>
      <br />
      <div>
        회원이름 입력 :{' '}
        <input
          name="candidateName"
          onChange={onInput}
          value={candidateName}
          onKeyPress={enterPressed}
        />
        <button onClick={saveButton}>저장</button>
      </div>
      <Team
        Agenre={Agenre}
        Bgenre={Bgenre}
        Cgenre={Cgenre}
        candidate={candidateName}
      />
    </>
  );
}

export default App;
