import React, { useEffect, useState } from 'react';

function Team({ Agenre, Bgenre, Cgenre }) {
  const candidates = ['1 ', '2 ', '3 ', '4 ', '5 ', '6 ', '7 ', '8 ', '9 '];

  const [team, setTeam] = useState({
    teamA: [],
    teamB: [],
    teamC: [],
  });
  const [oldTeam, setOldTeam] = useState({
    oldA: [],
    oldB: [],
    oldC: [],
  });
  const [state, setState] = useState({
    text: null,
  });
  const [oldCandidate, setOldCandidate] = useState({
    oldTeamArray: [],
  });

  const { oldA, oldB, oldC } = oldTeam;
  const { teamA, teamB, teamC } = team;
  const { oldTeamArray } = oldCandidate;
  const { text } = state;
  useEffect(() => {
    console.log(text, oldA, oldB, oldC);
    if (text === '중복발생') {
      onClick();
    }
    /* eslint-disable-next-line */
  }, [text]);

  const shuffle = (array) => {
    var currentIndex = array.length,
      temporaryValue,
      randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {
      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;

      // And swap it with the current element.
      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }

    return array;
  };
  const compareArray = (arr1, arr2) => {
    // 결과값
    var rst = false;

    // arr1 배열의 크기만큼 반복
    arr1.forEach(function (item) {
      // arr1 배열 아이템이, arr2 배열에 있는지 확인
      // 있으면, arr2에 item이 존재하는 index 리턴
      // 없으면, -1 리턴
      var i = arr2.indexOf(item);

      // 존재하면, splice함수를 이용해서 arr2 배열에서 item 삭제
      if (i > -1) arr2.splice(i, 1);
    });

    // compare2의 길이가 0이면 동일하다고 판단.
    rst = arr2.length === 1 || 0;

    return rst;
  };

  const onClick = () => {
    const shuffled = shuffle(candidates);
    const shuffledArray = shuffled.slice();
    setOldCandidate({
      oldTeamArray: shuffledArray,
    });
    const a = shuffled.splice(0, 3);
    const b = shuffled.splice(0, 3);
    const c = shuffled.splice(0, 3);

    setTeam({
      teamA: a,
      teamB: b,
      teamC: c,
    });
    const otA = oldTeamArray.splice(0, 3);
    const otB = oldTeamArray.splice(0, 3);
    const otC = oldTeamArray.splice(0, 3);

    setOldTeam({
      oldA: otA,
      oldB: otB,
      oldC: otC,
    });
    console.log(otA, otB, otC);
    const conditionA = compareArray(otA, a);
    const conditionB = compareArray(otB, b);
    const conditionC = compareArray(otC, c);
    if (conditionA || conditionB || conditionC) {
      console.log('should shuffle');
    }
  };

  // useEffect(()=>{
  //   while(conditionA)
  // })

  return (
    <>
      <div>
        <br />
        <br />
        {Agenre}팀: 임원A , {teamA} 전시즌 {Agenre}팀: 임원A , {oldA.join(', ')}
        <br />
        <br />
        {Bgenre}팀: 임원B , {teamB} 전시즌 {Bgenre}팀: 임원B , {oldB.join(', ')}
        <br />
        <br />
        {Cgenre}팀: 임원C , {teamC} 전시즌 {Cgenre}팀: 임원C , {oldC.join(', ')}
      </div>
      <button onClick={onClick}>Mix</button>
    </>
  );
}

export default Team;
