import React, { useMemo } from 'react';

function Team({ Agenre, Bgenre, Cgenre, onChange }) {
  const makeCandidates = (array) => {
    const newCandidates = array.sort((a, b) => {
      return parseInt(Math.random() * 10) - 5;
    });
    let teamA = newCandidates.splice(0, 4);
    let teamB = newCandidates.splice(0, 4);
    let teamC = newCandidates.splice(0, 4);
    return [teamA, teamB, teamC];
  };

  const candidates = [
    '민식',
    '무현',
    '대중',
    '기철',
    '회영',
    '민솔',
    '성묵',
    '희선',
    '대희',
    '영미',
    '소영',
    '알라',
  ];

  useMemo(() => {
    makeCandidates(candidates);
  }, makeCandidates);

  return (
    <>
      <div>
        {Agenre}팀: {newCandidates[0]}
        <br />
        {Bgenre}팀: {newCandidates[1]}
        <br />
        {Cgenre}팀: {newCandidates[2]}
      </div>
    </>
  );
}

export default Team;
