import React, { useState } from 'react';

function App() {
  const [ganres, setGanre] = useState({ Agenre: '', Bgenre: '', Cgenre: '' });

  const { Agenre, Bgenre, Cgenre } = ganres;

  const candidates = [
    '민식',
    '무현',
    '대중',
    '기철',
    '회영',
    '민솔',
    '성묵',
    '희선',
    '대희',
    '영미',
    '소영',
    '알라',
  ];
  const randomCandidates = candidates.sort((a, b) => {
    return parseInt(Math.random() * 10) - 5;
  });

  const teamA = randomCandidates.splice(0, 4);
  const newTeamA = teamA.join(', ');
  const teamB = randomCandidates.splice(0, 4);
  const newTeamB = teamB.join(', ');
  const teamC = randomCandidates.splice(0, 4);
  const newTeamC = teamC.join(', ');

  const onChange = (e) => {
    const { value, name } = e.target;
    setGanre({
      ...ganres,
    });
    console.log(value, name);
  };

  return (
    <>
      팀A 장르 :{' '}
      <input name="Agenre" onChange onChange={onChange} value={Agenre} />
      팀B 장르 : <input />
      팀C 장르 : <input />
      <div>
        <p>
          {Agenre}팀 {newTeamA}
          <br />
          {Bgenre}팀 {newTeamB}
          <br />
          {Cgenre}팀 {newTeamC}
        </p>
      </div>
    </>
  );
}

export default App;
