function App() {
  const [ganres, setGanre] = useState({ Agenre: '', Bgenre: '', Cgenre: '' });

  const { Agenre, Bgenre, Cgenre } = ganres;

  const onChange = (e) => {
    const { value, name } = e.target;
    setGanre({
      ...ganres,
      [name]: value,
    });
    console.log(value, name);
  };

  return (
    <>
      팀A 장르 :{' '}
      <input name="Agenre" onChange onChange={onChange} value={Agenre} />
      팀B 장르 :{' '}
      <input name="Bgenre" onChange onChange={onChange} value={Bgenre} />
      팀C 장르 :{' '}
      <input name="Bgenre" onChange onChange={onChange} value={Bgenre} />
      <div>
        <p>
          {Agenre}팀: {newTeamA}
          <br />
          {Bgenre}팀: {newTeamB}
          <br />
          {Cgenre}팀: {newTeamC}
        </p>
      </div>
    </>
  );
}

export default App;
