import React, { useState, useMemo } from 'react';

function Team({ Agenre, Bgenre, Cgenre }) {
  const candidates = ['1 ', '2 ', '3 ', '4 ', '5 ', '6 ', '7 ', '8 ', '9 '];

  const [team, setTeam] = useState({
    teamA: [],
    teamB: [],
    teamC: [],
    newCandidate: [],
  });
  const [oldTeam, setOldTeam] = useState({
    oldA: [],
    oldB: [],
    oldC: [],
  });
  const { teamA, teamB, teamC, newCandidate } = team;

  const { oldA, oldB, oldC } = oldTeam;

  const shuffle = (array) => {
    var currentIndex = array.length,
      temporaryValue,
      randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {
      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;

      // And swap it with the current element.
      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }

    return array;
  };

  const onClick = () => {
    const newCandidates = shuffle(candidates);
    const teamA = newCandidates.splice(0, 3);
    const teamAlpha = teamA.join(', ');
    const teamB = newCandidates.splice(0, 3);
    const teamBeta = teamB.join(', ');
    const teamC = newCandidates.splice(0, 3);
    const teamChallie = teamC.join(', ');
    setTeam({
      teamA: teamAlpha,
      teamB: teamBeta,
      teamC: teamChallie,
      newCandidate: teamA,
    });
    console.log(canddidateArray);
  };
  const oldTeams = useMemo(() => {
    setOldTeam({
      oldA: teamA,
      oldB: teamB,
      oldC: teamC,
    });
    return oldA;
  }, newCandidate);

  return (
    <>
      <div>
        {Agenre}팀: 임원A , {teamA}
        <br />
        {Bgenre}팀: 임원B , {teamB}
        <br />
        {Cgenre}팀: 임원C , {teamC}
      </div>
      <button onClick={onClick}>Mix</button>
    </>
  );
}

export default Team;
