import React, { useState } from 'react';

function Team({ Agenre, Bgenre, Cgenre }) {
  const candidates = [
    '효인',
    '민아',
    '소영',
    '주미',
    '태연',
    '민지',
    '성묵',
    '회영',
    '대희',
  ];

  const [team, setTeam] = useState({
    teamA: [],
    teamB: [],
    teamC: [],
  });

  const { teamA, teamB, teamC } = team;

  const makeCandidates = (array) => {
    const newCandidates = array.sort((a, b) => {
      return parseInt(Math.random() * 9) - 6;
    });
    return newCandidates;
  };

  const onClick = () => {
    const newCandidates = makeCandidates(candidates);
    const teamA = newCandidates.splice(0, 3);
    const teamAlpha = teamA.join(', ');
    const teamB = newCandidates.splice(0, 3);
    const teamBeta = teamB.join(', ');
    const teamC = newCandidates.splice(0, 3);
    const teamChallie = teamC.join(', ');
    setTeam({
      teamA: teamAlpha,
      teamB: teamBeta,
      teamC: teamChallie,
    });
  };

  return (
    <>
      <div>
        {Agenre}팀: 임원1, {teamA}
        <br />
        {Bgenre}팀: 임원2, {teamB}
        <br />
        {Cgenre}팀: 임원3, {teamC}
      </div>
      <button onClick={onClick}>Mix</button>
    </>
  );
}

export default Team;
